operation.add:
public function checkphpner_IfTableExist($table_name) {
  return $this->db->query("
    SELECT 
      COUNT(*) as total 
    FROM information_schema.TABLES 
    WHERE 
      TABLE_SCHEMA = '".DB_DATABASE."' 
    AND 
      TABLE_NAME = '".$table_name."'
  ")->row['total'];
}

public function checkIfColumnExist($table_name, $table_column) {
  return $this->db->query("
    SELECT
      COUNT(*) as total
    FROM information_schema.COLUMNS
    WHERE
      TABLE_SCHEMA = '".DB_DATABASE."'
    AND
      TABLE_NAME = '".$table_name."'
    AND
      COLUMN_NAME  = '".$table_column."'
  ")->row['total'];
}

public function phpner_conver_cyrillic($string, $replace = false) {
  $array = array(
    "А"=>"a",
    "Б"=>"b",
    "В"=>"v",
    "Г"=>"g",
    "Д"=>"d",
    "Е"=>"e",
    "Ё"=>"yo",
    "Ж"=>"zh",
    "З"=>"z",
    "И"=>"i",
    "Й"=>"j",
    "К"=>"k",
    "Л"=>"l",
    "М"=>"m",
    "Н"=>"n",
    "О"=>"o",
    "П"=>"p",
    "Р"=>"r",
    "С"=>"s",
    "Т"=>"t",
    "У"=>"u",
    "Ф"=>"f",
    "Х"=>"kh",
    "Ц"=>"ts",
    "Ч"=>"ch",
    "Ш"=>"sh",
    "Щ"=>"sch",
    "Ъ"=>"",
    "Ы"=>"y",
    "Ь"=>"",
    "Э"=>"e",
    "Ю"=>"yu",
    "Я"=>"ya",
    "а"=>"a",
    "б"=>"b",
    "в"=>"v",
    "г"=>"g",
    "д"=>"d",
    "е"=>"e",
    "ё"=>"yo",
    "ж"=>"zh",
    "з"=>"z",
    "и"=>"i",
    "й"=>"j",
    "к"=>"k",
    "л"=>"l",
    "м"=>"m",
    "н"=>"n",
    "о"=>"o",
    "п"=>"p",
    "р"=>"r",
    "с"=>"s",
    "т"=>"t",
    "у"=>"u",
    "ф"=>"f",
    "х"=>"kh",
    "ц"=>"ts",
    "ч"=>"ch",
    "ш"=>"sh",
    "щ"=>"sch",
    "ъ"=>"",
    "ы"=>"y",
    "ь"=>"",
    "э"=>"e",
    "ю"=>"yu",
    "я"=>"ya"
  );

  $string = preg_replace('/\s{2,}/ui', ' ', $string);
  $string = preg_replace('/[^a-zA-Zа-яА-Я0-9_\s]/ui', '', $string);
  $string = preg_replace('/\s/ui', '_', $string);

  if ($replace) {
    return strtr($this->phpner_normalize_string(trim($string), true), $array);
  } else {
    return strtr($this->phpner_normalize_string(trim($string)), $array);
  }
}

public function phpner_normalize_string($string, $replace = false, $lower = true) {
  $result = trim($string);

  if ($lower) {
    $result = mb_strtolower($result);
  }

  if ($replace) {
    $result = str_replace(" ", "_", $result);
  }

  return $result;
}

public function addphpner_ProductFilterData($product_id, $data, $delete = true) {
  $this->load->model('localisation/language');
  $phpner_languages = $this->model_localisation_language->getLanguages();
  $phpner_product_stickers_data = $this->config->get('phpner_product_stickers_data');
  $phpner_product_filter_status = $this->config->get('phpner_product_filter_status');

  if ($phpner_product_filter_status) {
    if (isset($phpner_product_stickers_data['status']) && $phpner_product_stickers_data['status']) {
      if ($phpner_languages && isset($data['phpner_product_stickers'])) {
        foreach ($phpner_languages as $phpner_language) {
          foreach ($data['phpner_product_stickers'] as $phpner_product_sticker_id) {
            if ($delete) {
              $this->db->query("
                DELETE 
                FROM ".DB_PREFIX."phpner_filter_product_sticker
                WHERE 
                  product_id = '".(int)$product_id."' 
                AND 
                  product_sticker_id = '".(int)$phpner_product_sticker_id."'
              ");
            }

            $phpner_sticker_info = $this->db->query("
              SELECT 
                DISTINCT * 
              FROM ".DB_PREFIX."phpner_product_stickers pst
              LEFT JOIN ".DB_PREFIX."phpner_product_stickers_description pstd ON (pst.product_sticker_id = pstd.product_sticker_id)
              WHERE 
                pst.product_sticker_id = '".(int)$phpner_product_sticker_id."'
              AND 
                pstd.language_id = '".(int)$phpner_language['language_id']."'
            ")->row;

            if ($phpner_sticker_info) {
              $this->db->query("
                INSERT INTO ".DB_PREFIX."phpner_filter_product_sticker
                SET 
                  product_id = '".(int)$product_id."', 
                  language_id  = '".(int)$phpner_language['language_id']."',
                  product_sticker_id = '".(int)$phpner_sticker_info['product_sticker_id']."',
                  product_sticker_value = '".$this->db->escape($this->phpner_normalize_string($phpner_sticker_info['text'], false, false))."',
                  product_sticker_value_mod = '".$this->db->escape($this->phpner_conver_cyrillic($phpner_sticker_info['text'], true))."_".(int)$phpner_sticker_info['product_sticker_id']."'
              ");
            }
          }
        }
      }
    }

    if ( isset($data['product_attribute']) ) {
      foreach ($data['product_attribute'] as $product_attribute) {
        if ($product_attribute['attribute_id']) {
          if ($delete) {
            $this->db->query("
              DELETE 
              FROM ".DB_PREFIX."phpner_filter_product_attribute
              WHERE 
                product_id = '".(int)$product_id."' 
              AND 
                attribute_id = '".(int)$product_attribute['attribute_id']."'
            ");
          }

          foreach ($product_attribute['product_attribute_description'] as $language_id => $product_attribute_description) {
            $phpner_attribute_data = $this->db->query("
              SELECT 
                a.attribute_group_id, 
                a.sort_order, 
                ad.name 
              FROM ".DB_PREFIX."attribute a 
              LEFT JOIN ".DB_PREFIX."attribute_description ad ON (a.attribute_id = ad.attribute_id) 
              WHERE 
                a.attribute_id = '".(int)$product_attribute['attribute_id']."' 
              AND 
                ad.language_id = '".(int)$language_id."'
            ")->row;

            if ($delete) {
              $this->db->query("
                DELETE 
                FROM ".DB_PREFIX."phpner_filter_product_attribute
                WHERE 
                  product_id = '".(int)$product_id."' 
                AND 
                  attribute_id = '".(int)$product_attribute['attribute_id']."' 
                AND 
                  language_id = '".(int)$language_id."'
              ");
            }

            if ($phpner_attribute_data) {
              $this->db->query("
                INSERT INTO ".DB_PREFIX."phpner_filter_product_attribute
                SET 
                  product_id = '".(int)$product_id."', 
                  attribute_id = '".(int)$product_attribute['attribute_id']."', 
                  attribute_group_id = '".(int)$phpner_attribute_data['attribute_group_id']."',
                  sort_order = '".(int)$phpner_attribute_data['sort_order']."',
                  language_id = '".(int)$language_id."', 
                  attribute_value = '".$this->db->escape($this->phpner_normalize_string($product_attribute_description['text'], false, false))."',
                  attribute_value_mod = '".$this->db->escape($this->phpner_conver_cyrillic($product_attribute_description['text'], true))."_".(int)$product_attribute['attribute_id']."',
                  attribute_name = '".$this->db->escape($this->phpner_normalize_string($phpner_attribute_data['name'], false, false))."',
                  attribute_name_mod = '".$this->db->escape($this->phpner_conver_cyrillic($phpner_attribute_data['name'], true))."_".(int)$product_attribute['attribute_id']."'
              ");
            }
          }
        }
      }
    }

    if (isset($data['product_option'])) {
      foreach ($data['product_option'] as $product_option) {
        if ($product_option['type'] == 'select' || $product_option['type'] == 'radio' || $product_option['type'] == 'checkbox' || $product_option['type'] == 'image') {
          if (isset($product_option['product_option_value'])) {
            if ($phpner_languages) {
              foreach ($phpner_languages as $phpner_language) {
                $phpner_option_info = $this->db->query("
                  SELECT * 
                  FROM `".DB_PREFIX."option` o 
                  LEFT JOIN ".DB_PREFIX."option_description od ON (o.option_id = od.option_id) 
                  WHERE 
                    o.option_id = '".(int)$product_option['option_id']."' 
                  AND 
                    od.language_id = '".(int)$phpner_language['language_id']."'
                ")->row;

                foreach ($product_option['product_option_value'] as $product_option_value) {
                  $phpner_option_value_info = $this->db->query("
                    SELECT * 
                    FROM ".DB_PREFIX."option_value ov 
                    LEFT JOIN ".DB_PREFIX."option_value_description ovd ON (ov.option_value_id = ovd.option_value_id) 
                    WHERE 
                      ov.option_value_id = '".(int)$product_option_value['option_value_id']."' 
                    AND 
                      ovd.language_id = '".(int)$phpner_language['language_id']."'
                  ")->row;

                  if ($phpner_option_value_info) {
                    $this->db->query("
                      INSERT INTO ".DB_PREFIX."phpner_filter_product_option
                      SET 
                        product_id = '".(int)$product_id."', 
                        language_id = '".(int)$phpner_language['language_id']."',
                        option_id = '".(int)$product_option['option_id']."', 
                        option_name = '".$this->db->escape($this->phpner_normalize_string($phpner_option_info['name'], false, false))."',
                        option_name_mod = '".$this->db->escape($this->phpner_conver_cyrillic($phpner_option_info['name'], true))."_".(int)$product_option['option_id']."',
                        option_value_id = '".(int)$product_option_value['option_value_id']."', 
                        option_value_name  = '".$this->db->escape($phpner_option_value_info['name'])."',
                        option_value_name_mod = '".$this->db->escape($this->phpner_conver_cyrillic($phpner_option_value_info['name'], true))."_".(int)$product_option['option_id']."',
                        option_value_image = '".$this->db->escape($phpner_option_value_info['image'])."'
                    ");
                  }
                }
              }
            }
          }
        }
      }
    }

    if (isset($data['product_filter'])) {
      foreach ($data['product_filter'] as $filter_id) {
        if ($phpner_languages) {
          foreach ($phpner_languages as $phpner_language) {
            $phpner_standard_info = $this->db->query("
              SELECT 
                *, 
                (SELECT 
                  name 
                FROM ".DB_PREFIX."filter_group_description fgd 
                WHERE 
                  f.filter_group_id = fgd.filter_group_id 
                AND 
                  fgd.language_id = '".(int)$phpner_language['language_id']."'
                ) AS `group` 
              FROM ".DB_PREFIX."filter f 
              LEFT JOIN ".DB_PREFIX."filter_description fd ON (f.filter_id = fd.filter_id) 
              WHERE 
                f.filter_id = '".(int)$filter_id."' 
              AND 
                fd.language_id = '".(int)$phpner_language['language_id']."'
            ")->row;

            if ($phpner_standard_info) {
              $this->db->query("
                INSERT INTO ".DB_PREFIX."phpner_filter_product_standard
                SET 
                  product_id = '".(int)$product_id."', 
                  sort_order = '".(int)$phpner_standard_info['sort_order']."',
                  language_id = '".(int)$phpner_language['language_id']."',
                  filter_id = '".(int)$filter_id."', 
                  filter_group_id = '".(int)$phpner_standard_info['filter_group_id']."',
                  filter_name = '".$this->db->escape($this->phpner_normalize_string($phpner_standard_info['group'], false, false))."',
                  filter_name_mod = '".$this->db->escape($this->phpner_conver_cyrillic($phpner_standard_info['group'], true))."_".(int)$phpner_standard_info['filter_group_id']."',
                  filter_value = '".$this->db->escape($this->phpner_normalize_string($phpner_standard_info['name'], false, false))."',
                  filter_value_mod = '".$this->db->escape($this->phpner_conver_cyrillic($phpner_standard_info['name'], true))."_".(int)$filter_id."'
              ");
            }
          }
        }
      }
    }

    if (isset($data['phpner_product_manufacturers'])) {
      if ($this->checkphpner_IfTableExist(DB_PREFIX."manufacturer_description") && $this->checkIfColumnExist(DB_PREFIX . "manufacturer_description", "name")) {
        foreach ($data['phpner_product_manufacturers'] as $manufacturer_id) {
          if ($phpner_languages) {
            foreach ($phpner_languages as $phpner_language) {
              $phpner_manufacturer_info = $this->db->query("
                SELECT 
                  m.manufacturer_id, 
                  md.name, 
                  m.image 
                FROM `".DB_PREFIX."manufacturer` m 
                LEFT JOIN ".DB_PREFIX."manufacturer_description md ON (m.manufacturer_id = md.manufacturer_id) 
                WHERE 
                  m.manufacturer_id = '".(int)$manufacturer_id."' 
                AND 
                  md.language_id = '".(int)$phpner_language['language_id']."'
              ")->row;

              if ($phpner_manufacturer_info) {
                $this->db->query("
                  INSERT INTO ".DB_PREFIX."phpner_filter_product_manufacturer
                  SET 
                    product_id = '".(int)$product_id."', 
                    language_id = '".(int)$phpner_language['language_id']."',
                    manufacturer_id = '".(int)$manufacturer_id."', 
                    manufacturer_name = '".$this->db->escape($this->phpner_normalize_string($phpner_manufacturer_info['name'], false, false))."',
                    manufacturer_image = '".$this->db->escape($phpner_manufacturer_info['image'])."'
                ");
              } 
            }
          }
        }
      } else {
        foreach ($data['phpner_product_manufacturers'] as $manufacturer_id) {
          if ($phpner_languages) {
            foreach ($phpner_languages as $phpner_language) {
              $phpner_manufacturer_info = $this->db->query("
                SELECT 
                  m.manufacturer_id, 
                  m.name, 
                  m.image 
                FROM `".DB_PREFIX."manufacturer` m 
                WHERE 
                  m.manufacturer_id = '".(int)$manufacturer_id."'
              ")->row;

              if ($phpner_manufacturer_info) {
                $this->db->query("
                  INSERT INTO ".DB_PREFIX."phpner_filter_product_manufacturer
                  SET 
                    product_id = '".(int)$product_id."', 
                    language_id = '".(int)$phpner_language['language_id']."',
                    manufacturer_id = '".(int)$manufacturer_id."', 
                    manufacturer_name = '".$this->db->escape($this->phpner_normalize_string($phpner_manufacturer_info['name'], false, false))."',
                    manufacturer_image = '".$this->db->escape($phpner_manufacturer_info['image'])."'
                ");
              } 
            }
          }
        }
      }
    }
  }
}

public function editphpner_ProductFilterData($product_id, $data) {
  $this->load->model('localisation/language');
  $phpner_languages = $this->model_localisation_language->getLanguages();
  $phpner_product_stickers_data = $this->config->get('phpner_product_stickers_data');
  $phpner_product_filter_status = $this->config->get('phpner_product_filter_status');

  if ($phpner_product_filter_status) {
    if (isset($phpner_product_stickers_data['status']) && $phpner_product_stickers_data['status']) {
      $this->db->query("DELETE FROM ".DB_PREFIX."phpner_filter_product_sticker WHERE product_id = '".(int)$product_id."'");

      if ($phpner_languages && isset($data['phpner_product_stickers'])) {
        foreach ($phpner_languages as $phpner_language) {
          foreach ($data['phpner_product_stickers'] as $phpner_product_sticker_id) {
            $this->db->query("
              DELETE 
              FROM ".DB_PREFIX."phpner_filter_product_sticker
              WHERE 
                product_id = '".(int)$product_id."' 
              AND 
                product_sticker_id = '".(int)$phpner_product_sticker_id."'
            ");

            $phpner_sticker_info = $this->db->query("
              SELECT 
                DISTINCT * 
              FROM ".DB_PREFIX."phpner_product_stickers pst
              LEFT JOIN ".DB_PREFIX."phpner_product_stickers_description pstd ON (pst.product_sticker_id = pstd.product_sticker_id)
              WHERE 
                pst.product_sticker_id = '".(int)$phpner_product_sticker_id."'
              AND 
                pstd.language_id = '".(int)$phpner_language['language_id']."'
            ")->row;

            if ($phpner_sticker_info) {
              $this->db->query("
                INSERT INTO ".DB_PREFIX."phpner_filter_product_sticker
                SET 
                  product_id = '".(int)$product_id."', 
                  language_id  = '".(int)$phpner_language['language_id']."',
                  product_sticker_id = '".(int)$phpner_sticker_info['product_sticker_id']."',
                  product_sticker_value = '".$this->db->escape($this->phpner_normalize_string($phpner_sticker_info['text'], false, false))."',
                  product_sticker_value_mod = '".$this->db->escape($this->phpner_conver_cyrillic($phpner_sticker_info['text'], true))."_".(int)$phpner_sticker_info['product_sticker_id']."'
              ");
            }
          }
        }
      }
    }

    $this->db->query("DELETE FROM ".DB_PREFIX."phpner_filter_product_attribute WHERE product_id = '".(int)$product_id."'");

    if (!empty($data['product_attribute'])) {
      foreach ($data['product_attribute'] as $product_attribute) {
        if ($product_attribute['attribute_id']) {
          $this->db->query("
            DELETE 
            FROM ".DB_PREFIX."phpner_filter_product_attribute
            WHERE 
              product_id = '".(int)$product_id."' 
            AND 
              attribute_id = '".(int)$product_attribute['attribute_id']."'
          ");

          foreach ($product_attribute['product_attribute_description'] as $language_id => $product_attribute_description) {
            $phpner_attribute_data = $this->db->query("
              SELECT 
                a.attribute_group_id, 
                a.sort_order, 
                ad.name 
              FROM ".DB_PREFIX."attribute a 
              LEFT JOIN ".DB_PREFIX."attribute_description ad ON (a.attribute_id = ad.attribute_id) 
              WHERE 
                a.attribute_id = '".(int)$product_attribute['attribute_id']."' 
              AND 
                ad.language_id = '".(int)$language_id."'
            ")->row;

            if ($phpner_attribute_data) {
              $this->db->query("
                INSERT INTO ".DB_PREFIX."phpner_filter_product_attribute
                SET 
                  product_id = '".(int)$product_id."', 
                  attribute_id = '".(int)$product_attribute['attribute_id']."', 
                  attribute_group_id = '".(int)$phpner_attribute_data['attribute_group_id']."',
                  sort_order = '".(int)$phpner_attribute_data['sort_order']."',
                  language_id = '".(int)$language_id."', 
                  attribute_value = '".$this->db->escape($this->phpner_normalize_string($product_attribute_description['text'], false, false))."',
                  attribute_value_mod = '".$this->db->escape($this->phpner_conver_cyrillic($product_attribute_description['text'], true))."_".(int)$product_attribute['attribute_id']."',
                  attribute_name = '".$this->db->escape($this->phpner_normalize_string($phpner_attribute_data['name'], false, false))."',
                  attribute_name_mod = '".$this->db->escape($this->phpner_conver_cyrillic($phpner_attribute_data['name'], true))."_".(int)$product_attribute['attribute_id']."'
              ");
            }
          }
        }
      }
    }

    $this->db->query("DELETE FROM ".DB_PREFIX."phpner_filter_product_option WHERE product_id = '".(int)$product_id."'");

    if (isset($data['product_option'])) {
      foreach ($data['product_option'] as $product_option) {
        if ($product_option['type'] == 'select' || $product_option['type'] == 'radio' || $product_option['type'] == 'checkbox' || $product_option['type'] == 'image') {
          if (isset($product_option['product_option_value'])) {
            if ($phpner_languages) {
              foreach ($phpner_languages as $phpner_language) {
                $phpner_option_info = $this->db->query("
                  SELECT * 
                  FROM `".DB_PREFIX."option` o 
                  LEFT JOIN ".DB_PREFIX."option_description od ON (o.option_id = od.option_id) 
                  WHERE 
                    o.option_id = '".(int)$product_option['option_id']."' 
                  AND 
                    od.language_id = '".(int)$phpner_language['language_id']."'
                ")->row;

                foreach ($product_option['product_option_value'] as $product_option_value) {
                  $phpner_option_value_info = $this->db->query("
                    SELECT * 
                    FROM ".DB_PREFIX."option_value ov 
                    LEFT JOIN ".DB_PREFIX."option_value_description ovd ON (ov.option_value_id = ovd.option_value_id) 
                    WHERE 
                      ov.option_value_id = '".(int)$product_option_value['option_value_id']."' 
                    AND 
                      ovd.language_id = '".(int)$phpner_language['language_id']."'
                  ")->row;

                  if ($phpner_option_value_info) {
                    $this->db->query("
                      INSERT INTO ".DB_PREFIX."phpner_filter_product_option
                      SET 
                        product_id = '".(int)$product_id."', 
                        language_id = '".(int)$phpner_language['language_id']."',
                        option_id = '".(int)$product_option['option_id']."', 
                        option_name = '".$this->db->escape($phpner_option_info['name'])."',
                        option_name_mod = '".$this->db->escape($this->phpner_conver_cyrillic($phpner_option_info['name'], true))."_".(int)$product_option['option_id']."',
                        option_value_id  = '".(int)$product_option_value['option_value_id']."', 
                        option_value_name  = '".$this->db->escape($phpner_option_value_info['name'])."',
                        option_value_name_mod = '".$this->db->escape($this->phpner_conver_cyrillic($phpner_option_value_info['name'], true))."_".(int)$product_option['option_id']."',
                        option_value_image = '".$this->db->escape($phpner_option_value_info['image'])."'
                    ");
                  }
                }
              }
            }
          }
        }
      }
    }

    $this->db->query("DELETE FROM ".DB_PREFIX."phpner_filter_product_standard WHERE product_id = '".(int)$product_id."'");

    if (isset($data['product_filter'])) {
      foreach ($data['product_filter'] as $filter_id) {
        if ($phpner_languages) {
          foreach ($phpner_languages as $phpner_language) {
            $phpner_standard_info = $this->db->query("
              SELECT 
                *, 
                (SELECT 
                  name 
                FROM ".DB_PREFIX."filter_group_description fgd 
                WHERE 
                  f.filter_group_id = fgd.filter_group_id 
                AND 
                  fgd.language_id = '".(int)$phpner_language['language_id']."'
                ) AS `group` 
              FROM ".DB_PREFIX."filter f 
              LEFT JOIN ".DB_PREFIX."filter_description fd ON (f.filter_id = fd.filter_id) 
              WHERE 
                f.filter_id = '".(int)$filter_id."' 
              AND 
                fd.language_id = '".(int)$phpner_language['language_id']."'
            ")->row;

            if ($phpner_standard_info) {
              $this->db->query("
                INSERT INTO ".DB_PREFIX."phpner_filter_product_standard
                SET 
                  product_id = '".(int)$product_id."', 
                  sort_order = '".(int)$phpner_standard_info['sort_order']."',
                  language_id = '".(int)$phpner_language['language_id']."',
                  filter_id = '".(int)$filter_id."', 
                  filter_group_id = '".(int)$phpner_standard_info['filter_group_id']."',
                  filter_name = '".$this->db->escape($this->phpner_normalize_string($phpner_standard_info['group'], false, false))."',
                  filter_name_mod = '".$this->db->escape($this->phpner_conver_cyrillic($phpner_standard_info['group'], true))."_".(int)$phpner_standard_info['filter_group_id']."',
                  filter_value = '".$this->db->escape($this->phpner_normalize_string($phpner_standard_info['name'], false, false))."',
                  filter_value_mod = '".$this->db->escape($this->phpner_conver_cyrillic($phpner_standard_info['name'], true))."_".(int)$filter_id."'
              ");
            }
          }
        }
      }
    }

    $this->db->query("DELETE FROM ".DB_PREFIX."phpner_filter_product_manufacturer WHERE product_id = '".(int)$product_id."'");

    if (isset($data['phpner_product_manufacturers'])) {
      if ($this->checkphpner_IfTableExist(DB_PREFIX."manufacturer_description") && $this->checkIfColumnExist(DB_PREFIX . "manufacturer_description", "name")) {
        foreach ($data['phpner_product_manufacturers'] as $manufacturer_id) {
          if ($phpner_languages) {
            foreach ($phpner_languages as $phpner_language) {
              $phpner_manufacturer_info = $this->db->query("
                SELECT 
                  m.manufacturer_id, 
                  md.name, 
                  m.image 
                FROM `".DB_PREFIX."manufacturer` m 
                LEFT JOIN ".DB_PREFIX."manufacturer_description md ON (m.manufacturer_id = md.manufacturer_id) 
                WHERE 
                  m.manufacturer_id = '".(int)$manufacturer_id."' 
                AND 
                  md.language_id = '".(int)$phpner_language['language_id']."'
              ")->row;

              if ($phpner_manufacturer_info) {
                $this->db->query("
                  INSERT INTO ".DB_PREFIX."phpner_filter_product_manufacturer
                  SET 
                    product_id = '".(int)$product_id."', 
                    language_id = '".(int)$phpner_language['language_id']."',
                    manufacturer_id = '".(int)$manufacturer_id."', 
                    manufacturer_name = '".$this->db->escape($this->phpner_normalize_string($phpner_manufacturer_info['name'], false, false))."',
                    manufacturer_image = '".$this->db->escape($phpner_manufacturer_info['image'])."'
                ");
              } 
            }
          }
        }
      } else {
        foreach ($data['phpner_product_manufacturers'] as $manufacturer_id) {
          if ($phpner_languages) {
            foreach ($phpner_languages as $phpner_language) {
              $phpner_manufacturer_info = $this->db->query("
                SELECT 
                  m.manufacturer_id, 
                  m.name, 
                  m.image 
                FROM `".DB_PREFIX."manufacturer` m 
                WHERE 
                  m.manufacturer_id = '".(int)$manufacturer_id."'
              ")->row;

              if ($phpner_manufacturer_info) {
                $this->db->query("
                  INSERT INTO ".DB_PREFIX."phpner_filter_product_manufacturer
                  SET 
                    product_id = '".(int)$product_id."', 
                    language_id = '".(int)$phpner_language['language_id']."',
                    manufacturer_id = '".(int)$manufacturer_id."', 
                    manufacturer_name = '".$this->db->escape($this->phpner_normalize_string($phpner_manufacturer_info['name'], false, false))."',
                    manufacturer_image = '".$this->db->escape($phpner_manufacturer_info['image'])."'
                ");
              } 
            }
          }
        }
      }
    }
  }
}

public function refreshphpner_ProductFilterData($type = false) {
  $query = $this->db->query("SELECT DISTINCT product_id FROM ".DB_PREFIX."product")->rows;

  if ($query) {
    if ($type == 'attribute') {
      $this->db->query("TRUNCATE ".DB_PREFIX."phpner_filter_product_attribute");
    }

    if ($type == 'filter') {
      $this->db->query("TRUNCATE ".DB_PREFIX."phpner_filter_product_standard");
    }

    if ($type == 'option') {
      $this->db->query("TRUNCATE ".DB_PREFIX."phpner_filter_product_option");
    }

    if ($type == 'stickers') {
      $this->db->query("TRUNCATE ".DB_PREFIX."phpner_filter_product_sticker");
    }

    if ($type == 'manufacturers') {
      $this->db->query("TRUNCATE ".DB_PREFIX."phpner_filter_product_manufacturer");
    }

    foreach ($query as $product) {
      $data = array();

      if ($type == 'attribute') {
        $data['product_attribute'] = $this->getProductAttributes($product['product_id']);
      }

      if ($type == 'filter') {
        $data['product_filter'] = $this->getProductFilters($product['product_id']);
      }

      if ($type == 'option') {
        $data['product_option'] = $this->getProductOptions($product['product_id']);
      }

      if ($type == 'stickers') {
        $data['phpner_product_stickers'] = $this->getphpner_ProductFilterStickers($product['product_id']);
      }

      if ($type == 'manufacturers') {
        $data['phpner_product_manufacturers'] = $this->getphpner_ProductFilterManufacturers($product['product_id']);
      }

      $this->addphpner_ProductFilterData($product['product_id'], $data, false);
    }
  }
}

public function getphpner_ProductFilterStickers($product_id) {
  $phpner_product_sticker_data = array();

  $phpner_product_stickers_data_config = $this->config->get('phpner_product_stickers_data');

  if (isset($phpner_product_stickers_data_config['status']) && $phpner_product_stickers_data_config['status']) {
    $query = $this->db->query("
      SELECT 
        phpner_product_stickers
      FROM ".DB_PREFIX."product 
      WHERE 
        product_id = '".(int)$product_id."'
    ")->rows;

    if ($query) {
      foreach ($query as $product) {
        if (isset($product['phpner_product_stickers']) && $product['phpner_product_stickers']) {
          $product_sticker_ids = unserialize($product['phpner_product_stickers']);
          if ($product_sticker_ids) {
            foreach ($product_sticker_ids as $product_sticker_id) {
              $phpner_product_sticker_data[] = $product_sticker_id;
            }
          }
        }
      }
    }
  }

  return $phpner_product_sticker_data;
}

public function getphpner_ProductFilterManufacturers($product_id) {
  $query = $this->db->query("
    SELECT 
      manufacturer_id 
    FROM ".DB_PREFIX."product 
    WHERE 
      product_id = '".(int)$product_id."'
  ")->rows;

  $phpner_product_manufacturer_data = array();

  if ($query) {
    foreach ($query as $product) {
      if (isset($product['manufacturer_id']) && $product['manufacturer_id']) {
        $phpner_product_manufacturer_data[] = $product['manufacturer_id'];
      }
    }
  }

  return $phpner_product_manufacturer_data;
}

