                preg_match('/s_vm_[a-z0-9_]{1,}/', $standard_get_values_res_array_value, $standard_group_id);
                preg_match('/s_vm_[a-z0-9_]{1,}=([a-z0-9_]{1,})/', $standard_get_values_res_array_value, $standard_group_value);

